sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	g_form.addInfoMessage("NeveCS - Client Script - Neve_PopulateEmailAddress >> onChange Reuested for");
   //Type appropriate comment here, and begin script below
	var cs = new GlideAjax('Neve_GetEmailAddress');
	cs.addParam('sysparm_name','getEmail');
	cs.addParam('sysparm_userID',g_form.getValue('u_requested_for'));
	cs.getXML(callback);

	function callback(response)
	{
		g_form.addInfoMessage("NeveCS - Client Script - Neve_PopulateEmailAddress >> callback");
		try
			{
		var email = response.responseXML.documentElement.getAttribute("answer");
		g_form.addInfoMessage('Email: '+email);
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email',email);
			}
		catch(er)
			{
			g_form.addErrorMessage(er);
			}
	}

}
