function onLoad() {
   //Type appropriate comment here, and begin script below
	try
		{
   if(g_form.isNewRecord())
   {
	//Sample JS Log  Starts
	jslog("The Priority value = " + g_form.getValue('priority'));
	jslog("The first name of the currently logged in user is " + g_user.firstName + ".");
	//Sample JS Log  Ends
	g_form.addInfoMessage("This is New Record");
	//g_form.setValue('u_reqsted_for',g_user.userID); //typo error in variable name requested for
	g_form.setValue('u_requested_for',g_user.userID);
	g_form.addInfoMessage("Reached next line");
	//  heeloWo(); // written to get exception
   }
		}
	catch(e)
		{
			g_form.addInfoMessage("Reached Exception");
			jslog("Exception :" + e.message);
		}
}
