sys_script_include.script:
var Neve_GetEmailAddress = Class.create();
Neve_GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getEmail: function() {
        try {
            gs.info("NeveSS-Called invoked for Neve_GetEmailAddress >> getEmail");

            var gr = new GlideRecord("sys_user");
            gr.get(this.getParameter('sysparm_userID'));
            gs.info("NeveSS- Parameter for getEmail" + this.getParameter('sysparm_userID'));
            gs.info("NeveSS- Email for Passed User " + gr.email);
            return gr.email;

        } catch (err) {
            gs.error('NeveSS Error in Neve_GetEmailAddress >> getEmail' + err);
        }
    },
    type: 'Neve_GetEmailAddress'
});
